package week1.day1;

public class Factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int input =10;
		int fact = 1;
		
		for (int i=1; i<=input;i++)
			
		{
			
			fact= fact*i;
			
		}
		
		System.out.println(" The factorial of the given Number is " + fact);
		
		
		
		

	}

}
